import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Counter = () => {
  const [count, setCount] = useState(0);
  const [show, setShow] = useState('true');

  // function to update
  const increment = () => {
    setCount((count) => {
      localStorage.setItem("count", Number(count) + 1);
      return Number(count) + 1;
    })
  };

  const decrement = () => {
    setCount((count) => {
      if (count > 0) {
        localStorage.setItem('count', Number(count) - 1);
        return Number(count) - 1;
      } else {

      }
    })
  }

  // useEffect to render once
  useEffect(() => {
    const storedCount = localStorage.getItem('count');
    if (storedCount) {// exists
      setCount(storedCount);
    }
  }, []);
  const navigate = useNavigate();

  return (
    <>
      <h1>Counter: {count} </h1>
      <div style={{ display: 'flex', flexDirection: 'column', margin: '0px 11em' }}>
        <button id="plus" onClick={increment}> + </button>
      </div>
      <div style={{ display: 'flex', flexDirection: 'column', margin: '1em 11em' }}>
        <button id="minus" onClick={decrement}> - </button>
      </div>

      <button onClick={() => { navigate('/') }}> Go Back </button>
    </>
  );
}

export default Counter;
